    Translator.add("publication.form.sharing", "<p>Le choix du partage vous permet de choisir avec qui vous partagez vos contenus<\/p>\n<p class='icon-user'>Moi : Seulement vous pouvez voir vos contenus (pour vous constituer un journal de chasse personnel)<\/p>\n<p class='icon-users'>Amis : Vous partagez avec vos amis de I-C<\/p>\n<p class='icon-leaf'>Membres I-C : Vous partagez avec l'ensemble de la communaut\u00e9 I-C<\/p>\n", "ic_help", "fr");
    Translator.add("publication.form.geoloc", "<p>La g\u00e9olocalisation permet de g\u00e9olocaliser vos m\u00e9dias :<\/p>\n<ul>\n<li>soit en r\u00e9cup\u00e9rant l'information depuis votre photo ou vid\u00e9o,<\/li>\n<li>soit en la pla\u00e7ant sur la carte.<\/li>\n<\/ul>\n", "ic_help", "fr");
    Translator.add("publication.form.landmark", "<p>La g\u00e9olocalisation permet de g\u00e9olocaliser vos m\u00e9dias :<\/p>\n<ul>\n<li>soit en r\u00e9cup\u00e9rant l'information depuis votre photo ou vid\u00e9o,<\/li>\n<li>soit en la pla\u00e7ant sur la carte.<\/li>\n<\/ul>\n", "ic_help", "fr");
